import React, { Component } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import "../../App.css";
import axios from 'axios';
import './SignUp.css';
import emailjs from 'emailjs-com';

class SignUp extends Component {
  constructor() {
    super();
    this.state = {
      fullName: "",
      username: "",
      email: "",
      password: "",
    };
    this.changeFullName = this.changeFullName.bind(this)
    this.changeEmail = this.changeEmail.bind(this)
    this.changeUsername = this.changeUsername.bind(this)
    this.changePassword = this.changePassword.bind(this)
    this.onSubmit = this.onSubmit.bind(this)
  }

  changeFullName(event) {
    this.setState({
      fullName: event.target.value,
    });
  }
  changeUsername(event) {
    this.setState({
      username: event.target.value,
    });
  }
  changeEmail(event) {
    this.setState({
      email: event.target.value,
    });
  }
  changePassword(event) {
    this.setState({
      password: event.target.value,
    });
  }

  onSubmit(event) {
    event.preventDefault();

    const registered = {
      fullName: this.state.fullName,
      username: this.state.username,
      email: this.state.email,
      password: this.state.password,
    };
    axios
      .post("http://localhost:4000/app/signup", registered)
      .then((response) => console.log(response.data));

    this.setState({
      fullName: "",
      username: "",
      email: "",
      password: "",
    });

    var a = document.getElementById("name").value
    var b = document.getElementById("uname").value
    var c = document.getElementById("email").value
    var d = document.getElementById("pass").value
    if(a === ""){
      alert("'Name' field cannot be empty!")
    }
    else if(b === ""){
      alert("'Username' field cannot be empty!")
    }
    else if(c === ""){
      alert("'Email' field cannot be empty!")
    }
    else if(d === ""){
      alert("'Password' field cannot be empty!")
    }
    else if(d.lenght<6){
      alert("Password should be atleast 7 characters long")
    }
    else{
      alert("Form submitted")
      window.location = 'http://localhost:3000/';
    }

    // sendEmail(e);
  }

  // sendEmail(e) {
  //   e.preventDefault();
  //   emailjs.sendForm("service_tz7ww4f", "template_aavnfar", e.target, "2UpZq43vzdRGy-zqL")
  //   .then((result) => {
  //     console.log(result.text);
  //   })
  //   .catch((error) =>{ 
  //     console.log(error.text)
  //   });
  // }

  render() {
    return (
      <div>
        <div className="container">
          {/* <image src='/images/sign-up.jpeg'/> */}
          <div className="form-div">
            <form onSubmit={this.onSubmit}>
              <div className="Sign-title">
                Sign-Up
              </div>
              <div className="name">
                <input
                  type="text"
                  label for="name"
                  id="name"
                  placeholder="Full Name"
                  onChange={this.changeFullName}
                  value={this.state.fullName}
                  className="form-control form-group" required
                />
              </div>

              <div className="uname">
                <input
                  type="text"
                  id="uname"
                  placeholder="Username"
                  onChange={this.changeUsername}
                  value={this.state.username}
                  className="form-control form-group"
                />
              </div>

              {/* <div className="icon-1">
              <i class="fa-solid fa-envelope-dot"></i>
              </div> */}
              <div className="email">
                <input
                  type="text"
                  id="email"
                  placeholder="Email"
                  onChange={this.changeEmail}
                  value={this.state.email}
                  className="form-control form-group"
                />
              </div>

              <div className="password">
                <input
                  type="password"
                  id="pass"
                  placeholder="Password"
                  onChange={this.changePassword}
                  value={this.state.password}
                  className="form-control form-group"
                />
              </div>

              <div className="submit">
                <input
                  type="submit"
                  className="btn btn-success btn-block"
                  value="Send"
                />
              </div>
            </form>
          </div>
        </div>
      </div>
    );
  }
}
export default SignUp;
